document.addEventListener("DOMContentLoaded", () => {
const currentLocation = window.location.search;
const sURL = currentLocation.replace("?live&url=", "");
const video = document.querySelector("#video");
const player = new OpenPlayerJS("video", {
    hls: {
      smoothQualityChange: true,
      startLevel: -1,
      overrideNative: true,  
      debug: false,
      p2pConfig: {
        live: true,
        getStats: function (
          totalP2PDownloaded,
          totalP2PUploaded,
          totalHTTPDownloaded
        ) {}
      }
    }
  });

  player.init();
  player.src = "https://donelfantastic.github.io/webiptv/web/" + sURL;
  player.load();

  try {
    player.play();
  } catch (err) {
    throw new Error("error");
  }

  var TRange = null;

  function findString(str) {
    if (parseInt(navigator.appVersion) < 4) return;
    var strFound;
    if (window.find) {
     
      strFound = self.find(str);
      if (!strFound) {
        strFound = self.find(str, 0, 1);
        while (self.find(str, 0, 1)) continue;
      }
    } else if (navigator.appName.indexOf("Microsoft") != -1) {

      if (TRange != null) {
        TRange.collapse(false);
        strFound = TRange.findText(str);
        if (strFound) TRange.select();
      }
      if (TRange == null || strFound == 0) {
        TRange = self.document.body.createTextRange();
        strFound = TRange.findText(str);
        if (strFound) TRange.select();
      }
    } else if (navigator.appName == "Opera") {
      alert("OPERA UNSUPPORTED");
      return;
    }
    if (!strFound) alert("NOT FOUND!");
    return;
  }
});
